size_t krb5tgs_max_data_len = 1024;

char *krb5tgs_split(char *ciphertext, int index, struct fmt_main *self)
{
	static char *ptr, *keeptr;
	int i;

	if (strnlen(ciphertext, LINE_BUFFER_SIZE) < LINE_BUFFER_SIZE &&
	    strstr(ciphertext, "$SOURCE_HASH$"))
		return ciphertext;
	ptr = mem_alloc_tiny(strlen(ciphertext) + FORMAT_TAG_LEN + 1, MEM_ALIGN_NONE);
	keeptr = ptr;

	if (strncmp(ciphertext, FORMAT_TAG, FORMAT_TAG_LEN) != 0) {
		memcpy(ptr, FORMAT_TAG, FORMAT_TAG_LEN);
		ptr += FORMAT_TAG_LEN;
	}

	for (i = 0; i < strlen(ciphertext) + 1; i++)
		ptr[i] = tolower(ARCH_INDEX(ciphertext[i]));

	return keeptr;
}

int krb5tgs_valid(char *ciphertext, struct fmt_main *self)
{
	char *p;
	char *ctcopy;
	char *keeptr;
	int extra;

	if (strncmp(ciphertext, FORMAT_TAG, FORMAT_TAG_LEN))
		return 0;

	ctcopy = xstrdup(ciphertext);
	keeptr = ctcopy;

	ctcopy += FORMAT_TAG_LEN;
	if (ctcopy[0] == '*') {			/* assume account's info provided */
		ctcopy++;
		p = strtokm(ctcopy, "*");
		ctcopy  = strtokm(NULL, "");
		if (!ctcopy || *ctcopy != '$')
			goto err;
		++ctcopy;	/* set after '$' */
		goto edata;
	}
	if (ctcopy[0] == '$')
		ctcopy++;

edata:
	/* assume checksum */
	if (((p = strtokm(ctcopy, "$")) == NULL) || strlen(p) != 32)
		goto err;

	/* assume edata2 following */
	if (((p = strtokm(NULL, "$")) == NULL))
		goto err;
	if (!ishex(p) || (hexlen(p, &extra) < (64 + 16) || extra))
		goto err;

	if ((strtokm(NULL, "$") != NULL))
		goto err;
	MEM_FREE(keeptr);
	return 1;

err:
	MEM_FREE(keeptr);
	return 0;
}

void *krb5tgs_get_salt(char *ciphertext)
{
	int i;
	krb5tgs_salt *psalt;
	static unsigned char *ptr;
	char *p;
	char *ctcopy;
	char *keeptr;

	if (!ptr)
		ptr = mem_alloc_tiny(sizeof(krb5tgs_salt*),sizeof(krb5tgs_salt*));

	ctcopy = xstrdup(ciphertext);
	keeptr = ctcopy;

	if (strncmp(ciphertext, FORMAT_TAG, FORMAT_TAG_LEN) == 0) {
		ctcopy += FORMAT_TAG_LEN;
		if (ctcopy[0] == '*') {
			ctcopy++;
			p = strtokm(ctcopy, "*");
			ctcopy += strlen(p) + 2;
			goto edata;
		}
		if (ctcopy[0]=='$')
			ctcopy++;
	}

edata:
	if (((p = strtokm(ctcopy, "$")) != NULL) && strlen(p) == 32) {	/* assume checksum */

		unsigned char edata1[16];
		for (i = 0; i < 16; i++) {
			edata1[i] =
				atoi16[ARCH_INDEX(p[i * 2])] * 16 +
				atoi16[ARCH_INDEX(p[i * 2 + 1])];
		}

		/* skip '$' */
		p += strlen(p) + 1;

		/* retrieve non-constant length of edata2 */
		for (i = 0; p[i] != '\0'; i++)
			;

		size_t edata2len = i / 2;
		if (edata2len > krb5tgs_max_data_len)
			krb5tgs_max_data_len = edata2len;

		psalt = mem_calloc(1, sizeof(krb5tgs_salt) + edata2len);
		memcpy(psalt->edata1, edata1, 16);
		psalt->edata2len = edata2len;

		for (i = 0; i < edata2len; i++) {	/* assume edata2 */
			psalt->edata2[i] =
				atoi16[ARCH_INDEX(p[i * 2])] * 16 +
				atoi16[ARCH_INDEX(p[i * 2 + 1])];
		}

		/* Set the JtR core linkage stuff for this dyna_salt */
		psalt->dsalt.salt_cmp_offset = SALT_CMP_OFF(krb5tgs_salt, edata1);
		psalt->dsalt.salt_cmp_size = SALT_CMP_SIZE(krb5tgs_salt, edata1, edata2len, psalt->edata2len);
		psalt->dsalt.salt_alloc_needs_free = 1;

		memcpy(ptr, &psalt, sizeof(krb5tgs_salt*));
	}
	MEM_FREE(keeptr);

	return (void*)ptr;
}
